import "./OneProducts.css";
import { useState, useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";

//ACTIONS
import { getProductOneDetails } from "../redux/actions/productActions";
import { addToCart } from "../redux/actions/productActions";

const OneProduct = ({ match, history }) => {
  const [qty, setQty] = useState(1);
  const dispatch = useDispatch();

  //traigo el estado del store y desestructuro las propiedades de las acciones de los reducers
  const productsDetails = useSelector((state) => state.getProductDetail);
  const { loading, error, product } = productsDetails;

  useEffect(() => {
    if (product && match.params.id !== product._id) {
      dispatch(getProductOneDetails(match.params.id));
    }
  }, [dispatch, product, match]);
  console.log(product, "prodddddddddd");
  return (
    <div className="prod">
      {loading ? (
        <h2>Cargando...</h2>
      ) : error ? (
        <h2>{error}</h2>
      ) : (
        <>
          <div className="product-left">
            <div className="image-left">
              <img src={product.image} alt={product.name} />
            </div>
            <div className="left-info">
              <p className="left-name">{product.name}</p>

              <p>{product.description}</p>
            </div>
            <div className="product-right">
              <div className="right-info">
                <p>
                  precio: <span>${product.price}</span>
                </p>
                <p>
                  status: <span>{product.stock}</span>
                </p>
                <p>
                  cabtidad:
                  <select>
                    <option value="1">1</option>
                    <option value="2">2</option>
                    <option value="3">3</option>
                    <option value="4">4</option>
                  </select>
                </p>
                <p>
                  <button type="button">Anadir al carrito</button>
                </p>
              </div>
            </div>
          </div>
        </>
      )}
    </div>
  );
};

export default OneProduct;
